import React, { useContext, useState } from "react";
import noteContext from "../context/notes/noteContext";
import Notes from "./Notes";

export default function AddNote() {
  const { addNote, editNote } = useContext(noteContext);
      const [note, setNote] = useState({title: "", description: "", tag: ""});
  const onClick = (e) => {
      e.preventDefault();
      addNote(note.title, note.description, note.tag);
  };
 
  const onChange = (e) => {
      // fuckyoubitch
      // this was very hard to understand but this is very useful
      setNote({...note, [e.target.name]: e.target.value});
  };

  return (
    <div>
      <div className="container">
        <h1>Add a note </h1>
        <form className="my-3">
          <div className="mb-3">
            <label htmlFor="exampleInputEmail1" className="form-label">
              Title
            </label>
            <input
              type="text"
              className="form-control"
              id="title"
              name="title"
              aria-describedby="emailHelp" onChange={onChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="description" className="form-label">
              Description
            </label>
            <input
              type="text"
              className="form-control"
              id="description" 
              name="description"
              onChange={onChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="tag" className="form-label">
              Tag
            </label>
            <input
              type="text"
              className="form-control"
              id="tag" 
              name="tag"
              onChange={onChange}
            />
          </div>
          <button disabled={note.title < 5} type="submit" className="btn btn-primary" onClick = {onClick}>
            Add note
          </button>
        </form>
      </div>
    </div>
  );
}
