import React, { useContext, useEffect, useRef } from 'react'
import noteContext from "../context/notes/noteContext";
import NoteItem from './NoteItem';
import AddNote from './AddNote';

export default function Notes() {
      const { notes, addNote, getNotes } = useContext(noteContext);
      useEffect(() => {
            getNotes()
      }, []);
      
      const ref = useRef(null);
      
      const updateNote = (note) => {
            console.log(ref.current);
            ref.current.click();
      };
      
  return (
      <>
      <AddNote />
<button type="button" ref={ref} className="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Launch demo modal
</button>
<div className="modal fade" id="exampleModal"  tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog" role="document">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">Edit Note</h5>
        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
        ...
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
      <div className="container py-3 my-1">
      <h1>Your notes</h1>
        <div className="row my-4">
          {notes.map((element) => {
            return (
              <div className="col-md-3 mb-5" key={element._id}>
                <NoteItem note = {element} updateNote = {updateNote}
                />
              </div>
            );
          })}
        </div>
        <div className="d-flex justify-content-between">
        </div>
      </div>
      </>
  )
}
