import { createContext } from "react";
import { useState } from "react";
import NoteContext from "./noteContext";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const notesInitial = [];

  const [notes, setNotes] = useState(notesInitial);

  // Get all notes
  const getNotes = async (title, description, tag) => {
    const response = await fetch(`${host}/api/notes/addnote/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjUwODM5ZjUxOThjMWIxMjk5MDVjMmQwIn0sImlhdCI6MTY5NTA2NDQxM30.yvIgCyHZ0awRBccM6EjaDwa2BOTAqvtqFK9fi0IAfLY",
      },
      body: JSON.stringify({ title, description, tag }),
    });

    // fuckyoubitch api call krna baki h
    const note = {
      _id: "651592e78e8447e5ce77ba7d",
      user: "650839f5198c1b129905c2d0",
      title: "komal kambe",
      description: "komal kambe.... This is a added note",
      tag: "komal kambe",
      date: "2023-09-28T14:51:19.682Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  // Add a Note
  const addNote = async (title, description, tag) => {
    const response = await fetch(`${host}/api/notes/addnote/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjUwODM5ZjUxOThjMWIxMjk5MDVjMmQwIn0sImlhdCI6MTY5NTA2NDQxM30.yvIgCyHZ0awRBccM6EjaDwa2BOTAqvtqFK9fi0IAfLY",
      },
      body: JSON.stringify({ title, description, tag }),
    });

    // fuckyoubitch api call krna baki h
    const note = {
      _id: "651592e78e8447e5ce77ba7d",
      user: "650839f5198c1b129905c2d0",
      title: "komal kambe",
      description: "komal kambe.... This is a added note",
      tag: "komal kambe",
      date: "2023-09-28T14:51:19.682Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  // Delete a Note
  const deleteNote = async (id) => {
    // fuckyoubitch api call krna baki h
    console.log("Deleting note with id ", id);
    const newNotes = notes.filter((note) => {
      return note._id != id;
    });
    setNotes(newNotes);
  };

  // Update a Note
  const editNote = async (id, title, description, tag) => {
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjUwODM5ZjUxOThjMWIxMjk5MDVjMmQwIn0sImlhdCI6MTY5NTA2NDQxM30.yvIgCyHZ0awRBccM6EjaDwa2BOTAqvtqFK9fi0IAfLY",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = await response.json();

    console.log("Edited note with id", id);
    let foundIndex = notes.findIndex((note) => note._id == id);
    let tempNotes = notes;
    tempNotes[foundIndex] = { title, description, tag };
    setNotes(tempNotes);
  };

  return (
    <NoteContext.Provider
      value={{ notes, setNotes, addNote, deleteNote, editNote }}
    >
      {props.children}
    </NoteContext.Provider>
  );
};

export default NoteState;
